  background(bg_color);

  fill(255, 0, 175);
  circle(a_x-a_width/2, 100, a_width)
  // circle(a_x, 100, a_width)

  if (a_crawl) {
    a_x += 1;
    if (a_x-a_width > width) {
    // if (a_x > width) {
      a_x = 0;
    }
  }
