/*** CLASS ***/

class Bsquare {
  constructor(x, y, r, bsp) {
    /*** new property ***/
    this.color = random(0, 255);

    // this.color = (random(100,250),200,0);
    // NO WORKY

    this.x = x;
    this.y = y;
    this.r = r;
    this.xsp = bsp; // xspeed
    this.ysp = bsp; // yspeed

    // fill(this.color);
  }

  bounceRandom() {
    this.move();
    this.colorIt();
    this.show();
  }

  /*** new function inside class ***/

  colorIt() {
    fill(this.color);
  }

  //*** jht addition ***//
  changeColor() {
    this.color = [random(100,250),0,0];
  }
  //***             ***//

  // display ball
  show() {
    stroke(150, 150, 200);
    strokeWeight(4);
    //  noFill();
    rect(this.x - this.r, this.y - this.r, this.r * 2);
  }

  // move and bounce ball
  move() {
    // move ball
    this.x = this.x + this.xsp;
    this.y = this.y + this.ysp;

    // bounce x
    if (this.x >= width - this.r || this.x <= 0 + this.r) {
      this.xsp = -this.xsp;
  //*** jht addition ***//      
      this.changeColor();
  //***             ***//
    }

    // bounce y
    if (this.y >= height - this.r || this.y <= 0 + this.r) {
      this.ysp = -this.ysp;
    }
  }
}

