function cornerBounce() {
  if (bb.x >= width - bb.radius || bb.x <= bb.radius) {
    bb.xspeed = -bb.xspeed;
  }
  if (bb.y >= height - bb.radius || bb.y - bb.radius <= 0) {
    bb.yspeed = -bb.yspeed;
  }
}

function moveIt() {
  bb.x = bb.x + bb.xspeed;
  bb.y = bb.y + bb.yspeed;
}

